INSERT INTO `wp_wpjb_job` (`id`, `employer_id`, `job_title`, `job_slug`, `job_description`, `job_created_at`, `job_modified_at`, `job_expires_at`, `job_country`, `job_state`, `job_zip_code`, `job_city`, `company_name`, `company_url`, `company_email`, `is_approved`, `is_active`, `is_filled`, `is_featured`, `applications`, `read`, `cache`) VALUES
(1, NULL, 'Multimedia Designer', 'multimedia-designer', '<p>Sparxoo is an integrated digital agency. Recognized as Small Business of the Year Finalist, Sparxoo is among the fastest growing agencies in Florida, with offices in Tampa and Miami. As part of our growth, we continue to bring on talented emerging leaders who are fearless and resourceful in applying new solutions as a catalyst for change in an always on digital world.</p>\r\n<p>We are looking for a talented Multimedia Designer to join the Sparxoo team. Our ideal candidate is a self-motivated, dynamic, creative thinker with experience crafting and designing for digital campaigns that drive real engagement through cohesive strategies integrating websites, mobile, video, content, social media, along with a tie-back to traditional media.</p>\r\n<p><strong><span style="text-decoration: underline;">Responsibilities</span></strong></p>\r\n<ul>\r\n<li><strong>Interactive Design.</strong> You will work in collaboration with Senior Interactive Art Director to conceive of, develop, and review concepts at each stage of design and production. You will energize brands as you create innovative and appealing designs in a fast-paced, rapidly changing environment, through sketches, storyboards, user task flows, wireframes, prototypes, and the production of final assets for specific features and projects.</li>\r\n</ul>\r\n<ul>\r\n<li><strong>Creative Storytelling.</strong> We dream big and don’t settle for the status quo. We are counting on you to bring ideas ahead of the trend for digital design and multimedia storytelling best practices. We are looking for dynamic brand storytelling as you apply these ideas and your excellence to develop creative concepts, visual designs, and interactive campaign elements for digital marketing.</li>\r\n</ul>\r\n<ul>\r\n<li><strong>Technical Excellence.</strong> Behind your extraordinary creativity, you have a strong foundation and high standards of technical excellence. You ensure that agency creative is produced with the highest standards, executed with elegant, brand-consistent, user-centric design solutions for digital projects. You are detail oriented down to clean layouts, purposeful color, and versioning.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;">REQUIREMENTS</span></strong></p>\r\n<ul>\r\n<li>2&nbsp;- 4&nbsp;years professional experience in an agency or in-house creative department</li>\r\n<li>Must be comfortable working in a range of digital mediums, including web &amp; mobile design, video, email, banner ads, and social-media applications</li>\r\n<li>Video editing skills preferred</li>\r\n<li>Experience working with a CMS preferred</li>\r\n<li>Interaction design skills: experience creating wireframes, site/system flows, site architecture diagrams, etc desired</li>\r\n<li>Communicate your design thinking in a way that works for all the parties involved (engineers, project managers, clients, etc.) through appropriately detailed specifications and reviews</li>\r\n<li>Advanced knowledge of Adobe CS (Photoshop, Illustrator, InDesign; Premier or equivalent a plus)</li>\r\n<li>4-year Bachelor''s Degree in your creative specialization (such as Fine Arts, Media Arts, Interaction Design) preferred</li>\r\n</ul>', '2015-04-20', '2015-04-20', '2015-05-20', 840, 'Florida', '', 'Tampa', 'Sparxoo', '', 'joe@saprxoo.com', 1, 1, 0, 0, 0, 1, ''),
(2, NULL, 'Marketing Intern', 'marketing-intern', 'Marketing Intern', '2015-04-20', '2015-04-20', '2015-05-20', 840, 'Florida', '', 'Tampa', 'Sparxoo', '', 'joe@saprxoo.com', 1, 1, 0, 0, 0, 1, ''),
(3, NULL, 'Manager, Social Marketing', 'manager-social-marketing', 'Manager, Social Marketing', '2015-04-20', '2015-04-21', '2015-05-20', 840, 'Florida', '', 'Tampa', 'Sparxoo', '', 'joe@saprxoo.com', 1, 1, 0, 0, 0, 1, '');
INSERT INTO `wp_wpjb_job_search` (`id`, `job_id`, `title`, `description`, `company`, `location`) VALUES
(1, 1, 'Multimedia Designer', 'Sparxoo is an integrated digital agency. Recognized as Small Business of the Year Finalist, Sparxoo is among the fastest growing agencies in Florida, with offices in Tampa and Miami. As part of our growth, we continue to bring on talented emerging leaders who are fearless and resourceful in applying new solutions as a catalyst for change in an always on digital world.\r\nWe are looking for a talented Multimedia Designer to join the Sparxoo team. Our ideal candidate is a self-motivated, dynamic, creative thinker with experience crafting and designing for digital campaigns that drive real engagement through cohesive strategies integrating websites, mobile, video, content, social media, along with a tie-back to traditional media.\r\nResponsibilities\r\n\r\nInteractive Design. You will work in collaboration with Senior Interactive Art Director to conceive of, develop, and review concepts at each stage of design and production. You will energize brands as you create innovative and appealing designs in a fast-paced, rapidly changing environment, through sketches, storyboards, user task flows, wireframes, prototypes, and the production of final assets for specific features and projects.\r\n\r\n\r\nCreative Storytelling. We dream big and don’t settle for the status quo. We are counting on you to bring ideas ahead of the trend for digital design and multimedia storytelling best practices. We are looking for dynamic brand storytelling as you apply these ideas and your excellence to develop creative concepts, visual designs, and interactive campaign elements for digital marketing.\r\n\r\n\r\nTechnical Excellence. Behind your extraordinary creativity, you have a strong foundation and high standards of technical excellence. You ensure that agency creative is produced with the highest standards, executed with elegant, brand-consistent, user-centric design solutions for digital projects. You are detail oriented down to clean layouts, purposeful color, and versioning.\r\n\r\n&nbsp;\r\nREQUIREMENTS\r\n\r\n2&nbsp;- 4&nbsp;years professional experience in an agency or in-house creative department\r\nMust be comfortable working in a range of digital mediums, including web &amp; mobile design, video, email, banner ads, and social-media applications\r\nVideo editing skills preferred\r\nExperience working with a CMS preferred\r\nInteraction design skills: experience creating wireframes, site/system flows, site architecture diagrams, etc desired\r\nCommunicate your design thinking in a way that works for all the parties involved (engineers, project managers, clients, etc.) through appropriately detailed specifications and reviews\r\nAdvanced knowledge of Adobe CS (Photoshop, Illustrator, InDesign; Premier or equivalent a plus)\r\n4-year Bachelor''s Degree in your creative specialization (such as Fine Arts, Media Arts, Interaction Design) preferred\r\n', 'Sparxoo', 'US USA United States Florida Tampa '),
(2, 2, 'Marketing Intern', 'Marketing Intern', 'Sparxoo', 'US USA United States Florida Tampa '),
