INSERT INTO `wp_wpjb_job` (`id`, `employer_id`, `job_title`, `job_slug`, `job_description`, `job_created_at`, `job_modified_at`, `job_expires_at`, `job_country`, `job_state`, `job_zip_code`, `job_city`, `company_name`, `company_url`, `company_email`, `is_approved`, `is_active`, `is_filled`, `is_featured`, `applications`, `read`, `cache`) VALUES
(1, NULL, 'Multimedia Designer', 'multimedia-designer', '<p>Sparxoo is an integrated digital agency. Recognized as Small Business of the Year Finalist, Sparxoo is among the fastest growing agencies in Florida, with offices in Tampa and Miami. As part of our growth, we continue to bring on talented emerging leaders who are fearless and resourceful in applying new solutions as a catalyst for change in an always on digital world.</p>\r\n<p>We are looking for a talented Multimedia Designer to join the Sparxoo team. Our ideal candidate is a self-motivated, dynamic, creative thinker with experience crafting and designing for digital campaigns that drive real engagement through cohesive strategies integrating websites, mobile, video, content, social media, along with a tie-back to traditional media.</p>\r\n<p><strong><span style="text-decoration: underline;">Responsibilities</span></strong></p>\r\n<ul>\r\n<li><strong>Interactive Design.</strong> You will work in collaboration with Senior Interactive Art Director to conceive of, develop, and review concepts at each stage of design and production. You will energize brands as you create innovative and appealing designs in a fast-paced, rapidly changing environment, through sketches, storyboards, user task flows, wireframes, prototypes, and the production of final assets for specific features and projects.</li>\r\n</ul>\r\n<ul>\r\n<li><strong>Creative Storytelling.</strong> We dream big and dont settle for the status quo. We are counting on you to bring ideas ahead of the trend for digital design and multimedia storytelling best practices. We are looking for dynamic brand storytelling as you apply these ideas and your excellence to develop creative concepts, visual designs, and interactive campaign elements for digital marketing.</li>\r\n</ul>\r\n<ul>\r\n<li><strong>Technical Excellence.</strong> Behind your extraordinary creativity, you have a strong foundation and high standards of technical excellence. You ensure that agency creative is produced with the highest standards, executed with elegant, brand-consistent, user-centric design solutions for digital projects. You are detail oriented down to clean layouts, purposeful color, and versioning.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="text-decoration: underline;">REQUIREMENTS</span></strong></p>\r\n<ul>\r\n<li>2&nbsp;- 4&nbsp;years professional experience in an agency or in-house creative department</li>\r\n<li>Must be comfortable working in a range of digital mediums, including web &amp; mobile design, video, email, banner ads, and social-media applications</li>\r\n<li>Video editing skills preferred</li>\r\n<li>Experience working with a CMS preferred</li>\r\n<li>Interaction design skills: experience creating wireframes, site/system flows, site architecture diagrams, etc desired</li>\r\n<li>Communicate your design thinking in a way that works for all the parties involved (engineers, project managers, clients, etc.) through appropriately detailed specifications and reviews</li>\r\n<li>Advanced knowledge of Adobe CS (Photoshop, Illustrator, InDesign; Premier or equivalent a plus)</li>\r\n<li>4-year Bachelor''s Degree in your creative specialization (such as Fine Arts, Media Arts, Interaction Design) preferred</li>\r\n</ul>', '2015-04-20', '2015-04-20', '2015-05-20', 840, 'Florida', '', 'Tampa', 'Sparxoo', '', 'joe@saprxoo.com', 1, 1, 0, 0, 0, 1, ''),
(2, NULL, 'Marketing Intern', 'marketing-intern', 'Marketing Intern', '2015-04-20', '2015-04-20', '2015-05-20', 840, 'Florida', '', 'Tampa', 'Sparxoo', '', 'joe@saprxoo.com', 1, 1, 0, 0, 0, 1, ''),
(3, NULL, 'Manager, Social Marketing', 'manager-social-marketing', 'Manager, Social Marketing', '2015-04-20', '2015-04-21', '2015-05-20', 840, 'Florida', '', 'Tampa', 'Sparxoo', '', 'joe@saprxoo.com', 1, 1, 0, 0, 0, 1, '');
INSERT INTO `wp_wpjb_job_search` (`id`, `job_id`, `title`, `description`, `company`, `location`) VALUES
(1, 1, 'Multimedia Designer', 'Sparxoo is an integrated digital agency. Recognized as Small Business of the Year Finalist, Sparxoo is among the fastest growing agencies in Florida, with offices in Tampa and Miami. As part of our growth, we continue to bring on talented emerging leaders who are fearless and resourceful in applying new solutions as a catalyst for change in an always on digital world.\r\nWe are looking for a talented Multimedia Designer to join the Sparxoo team. Our ideal candidate is a self-motivated, dynamic, creative thinker with experience crafting and designing for digital campaigns that drive real engagement through cohesive strategies integrating websites, mobile, video, content, social media, along with a tie-back to traditional media.\r\nResponsibilities\r\n\r\nInteractive Design. You will work in collaboration with Senior Interactive Art Director to conceive of, develop, and review concepts at each stage of design and production. You will energize brands as you create innovative and appealing designs in a fast-paced, rapidly changing environment, through sketches, storyboards, user task flows, wireframes, prototypes, and the production of final assets for specific features and projects.\r\n\r\n\r\nCreative Storytelling. We dream big and dont settle for the status quo. We are counting on you to bring ideas ahead of the trend for digital design and multimedia storytelling best practices. We are looking for dynamic brand storytelling as you apply these ideas and your excellence to develop creative concepts, visual designs, and interactive campaign elements for digital marketing.\r\n\r\n\r\nTechnical Excellence. Behind your extraordinary creativity, you have a strong foundation and high standards of technical excellence. You ensure that agency creative is produced with the highest standards, executed with elegant, brand-consistent, user-centric design solutions for digital projects. You are detail oriented down to clean layouts, purposeful color, and versioning.\r\n\r\n&nbsp;\r\nREQUIREMENTS\r\n\r\n2&nbsp;- 4&nbsp;years professional experience in an agency or in-house creative department\r\nMust be comfortable working in a range of digital mediums, including web &amp; mobile design, video, email, banner ads, and social-media applications\r\nVideo editing skills preferred\r\nExperience working with a CMS preferred\r\nInteraction design skills: experience creating wireframes, site/system flows, site architecture diagrams, etc desired\r\nCommunicate your design thinking in a way that works for all the parties involved (engineers, project managers, clients, etc.) through appropriately detailed specifications and reviews\r\nAdvanced knowledge of Adobe CS (Photoshop, Illustrator, InDesign; Premier or equivalent a plus)\r\n4-year Bachelor''s Degree in your creative specialization (such as Fine Arts, Media Arts, Interaction Design) preferred\r\n', 'Sparxoo', 'US USA United States Florida Tampa '),
(2, 2, 'Marketing Intern', 'Marketing Intern', 'Sparxoo', 'US USA United States Florida Tampa '),
(3, 3, 'Manager, Social Marketing', 'Manager, Social Marketing', 'Sparxoo', 'US USA United States Florida Tampa ');
INSERT INTO `wp_wpjb_mail` (`id`, `name`, `is_active`, `sent_to`, `format`, `mail_title`, `mail_body_text`, `mail_body_html`, `mail_from`, `mail_from_name`, `mail_bcc`) VALUES
(1, 'notify_admin_new_job', 1, 1, 'text/plain', 'New Job has been posted.', 'Greetings!\r\n\r\nThis is an automated email letting you know a new job has been\r\nposted to the job board. You can read the details below:\r\n\r\nThe job "{$job.job_title}" ({$job.id}) has been posted in {$category} {loop="job.tag.category"}{$value.title}{/loop} by ({$job.company_name} {$job.company_email}). \r\n\r\n{if condition="$payment.id>0"} Listing cost: {function="wpjb_price($payment.payment_sum, $payment.payment_currency)"} \r\n{else}\r\nThe listing was posted as a free listing. \r\n{/if}\r\n\r\nRead the original job listing here: {$job.url}\r\nSee this listing as an administrator here: {$job.admin_url}\r\n\r\nBest regards,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(2, 'notify_admin_payment_received', 1, 1, 'text/plain', 'Payment Received.', 'Hi there!\r\n\r\nThis is an automated email notifying you that a client has sent you\r\na payment. Read the details below.\r\n\r\nYou have received {$payment.payment_sum} from {$payment.user_id}\r\n({$payment.email}) for the listing {$payment.id}.\r\n\r\nBest regards,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(3, 'notify_employer_new_job', 1, 2, 'text/plain', 'Your job listing has been saved.', 'Hello!\r\n\r\nThis is an automated message from the WordPress JobBoard\r\nletting you know your job listing has been saved. You can read the\r\ndetails below:\r\n\r\nYour job listing titled "{$job.job_title}" has been saved. Once\r\nactivated by an administrator, your listing will be visible at:\r\n{$job.url} This listing will be visible until {$job.job_expires_at}.\r\n\r\nBest regards,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(5, 'notify_employer_job_expires', 1, 2, 'text/plain', 'Listing will expire soon.', 'Hi there,\r\n\r\nThis is a friendly automated reminder from the WordPress JobBoard. We just wanted to let you know that your job listing for the {$job.job_title} position will expire soon --- on {$job.job_expires_at}, to be exact.\r\n\r\nNeed to see the listing? Click here: {$job.url}\r\n\r\nBest regards,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(6, 'notify_admin_new_application', 1, 1, 'text/plain', 'Application for: {$job.job_title}', 'Hello!\r\n\r\nThis is an automated email from the WordPress JobBoard Support team. We just wanted to let you know that an application for the {$job.job_title} position with the company {$job.company_name} has been submitted. Read the details below:\r\n\r\nOriginal job listing: {$job.url}\r\nApplication ID: {$application.id}\r\nApplicant email address: {$application.email}\r\nApplicant message: \r\n{$application.message}\r\n\r\nBest regards,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(8, 'notify_applicant_applied', 1, 3, 'text/plain', 'Your application has been sent', 'Hey there,\r\n\r\nThis is an automated email from the WordPress JobBoard Support team letting you know your job application for the {$job.job_title} position with {$job.company_name} has been sent successfully.\r\n\r\nIf the employer is interested in your qualifications or would like to set up an interview, he or she will contact you shortly.\r\n\r\nYou can view the original job listing here: {$job.url}\r\n\r\nThank you for using the WordPress Job Board. Good luck!\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(9, 'notify_employer_register', 1, 2, 'text/plain', 'Your login and password', 'Hello!\r\n\r\nWelcome to the WordPress JobBoard. We''re happy to have you. Below you''ll find your username and password --- just in case you forget.\r\n\r\nUsername: {$username}\r\nPassword: {$password}\r\n\r\nReady to get started? Click here to log in: {$login_url}\r\n\r\nHappy hunting,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(10, 'notify_canditate_register', 1, 3, 'text/plain', 'Your login and password', 'Hello!\r\n\r\nWelcome to the WordPress JobBoard. We''re happy to have you. Below you''ll find your username and password --- just in case you forget.\r\n\r\nUsername: {$username}\r\nPassword: {$password}\r\n\r\nReady to get started? Click here to log in: {$login_url}\r\n\r\nHappy hunting,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(13, 'notify_admin_grant_access', 1, 1, 'text/plain', 'Employer requesting verification', 'Hi there,\r\n\r\nThis is an automated email from the WordPress JobBoard Support Team letting you know that an employer has requested access to resumes. Read the details below:\r\n\r\nEmployer requesting verification: {$company.company_name} (Company ID: {$company.id})\r\n\r\nEmployer email: {$company.email}\r\n\r\nYou can view (and approve) the company profile here:\r\n{$company_edit_url}.\r\n\r\nBest regards,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(14, 'notify_employer_verify', 1, 2, 'text/plain', 'Verification request', 'Hello,\r\n\r\nYou are receiving this email because you requested manual verification for a company profile. \r\n{if condition="$company.is_verified==1"} Your account was verified successfully. You now have full access to resumes. {else} \r\nThe administrator has declined you access to resumes. Try updating your profile and requesting verification again.{/if}\r\n\r\nBest regards,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(15, 'notify_employer_new_application', 1, 2, 'text/plain', 'Application for position {$job.job_title}', 'Hi there,\r\n\r\nThis is an automated email from the WordPress JobBoard Support team letting you know that an application (Applicant ID: {$application.id}) for the {$job.job_title} position you posted on {$job.job_created_at} has been submitted.\r\n\r\nSee the original listing here: {$job.url}\r\n\r\nBest regards,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(16, 'notify_job_alerts', 1, 4, 'text/plain', 'Your email alert.', 'Greetings from the WordPress JobBoard!\r\n\r\nHere are some new job listings for you to check out.\r\n\r\n{loop="jobs"}\r\n {$value.job_title} at {$value.company_name}\r\n {$value.url}\r\n --------------------------------------------------------------\r\n{/loop}\r\n\r\nNo longer wish to receive email notifications about new postings on the WordPress JobBoard? Click here to unsubscribe from this list: \r\n{$unsubscribe_url}\r\n\r\nBest regards,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(17, 'notify_employer_job_paid', 1, 2, 'text/plain', 'Your job has been activated', 'Hi there,\r\n\r\nThis is an automated email from the WordPress JobBoard Support team letting you know your job "{$job.job_title}" has been activated.\r\n\r\nYou can see it live at: {$job.url}\r\n\r\nBest regards,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(18, 'notify_employer_resume_paid', 1, 2, 'text/plain', 'Your resume access has been granted.', 'Hi there,\r\n\r\nThis is an automated email from the WordPress JobBoard Support team letting you know you have been granted access to "{$resume.headline}" resume.\r\n\r\nYou can see it live at:\r\n{$resume_unique_url}\r\n\r\nBest regards,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(19, 'notify_applicant_status_change', 1, 3, 'text/plain', 'Application status changed', 'Hi there!\r\n\r\nThis is an automated email notifying you that your application for job "{$job.job_title}" has been reviewed. Your application status has been changed to {$status}.\r\n\r\nBest regards,\r\nJob Board Support', '', 'test@example.com', 'Admin', ''),
(20, 'notify_admin_general_application', 1, 1, 'text/plain', 'New general application.', 'Hello!\r\n\r\nThis is an automated email from the WordPress JobBoard Support team. We just wanted to let you know that new general application has been submitted. Read the details below:\r\n\r\nApplication ID: {$application.id}\r\nApplicant email address: {$application.email}\r\nApplicant message: \r\n{$application.message}\r\n\r\nBest regards,\r\nJob Board Support', '', 'test@example.com', 'Admin', '');
